mapper.update:
UPDATE category
type = #{type}, `name` = #{name}, sort = #{sort}, `status` = #{status}, create_time = #{createTime}, create_user = #{createUser}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id};

mapper.select:
SELECT * FROM category

    AND type = #{type}
    AND `name` LIKE CONCAT('%', #{name}, '%')
