mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

mapper.select:
SELECT * FROM orders

    AND id = #{id}
    AND `number` = #{number}
    AND user_id = #{userId}
    AND address_book_id = #{addressBookId}

SELECT * FROM orders

    AND `number` = #{number}
    AND phone = #{phone}
    AND `status` = #{status}
    AND user_id = #{userId}

ORDER BY order_time DESC

SELECT * FROM orders
AND `number` LIKE CONCAT('%', #{number}, '%') AND phone LIKE CONCAT('%', #{phone}, '%') AND `status` = #{status} AND #{beginTime} <= order_time AND order_time <= #{endTime}
ORDER BY order_time DESC

SELECT COUNT(id) FROM orders
AND `number` LIKE CONCAT('%', #{number}, '%') AND phone LIKE CONCAT('%', #{phone}, '%') AND `status` = #{status}
ORDER BY order_time DESC
