mapper.update:
UPDATE shopping_cart
`name` = #{name}, image = #{image}, dish_flavor = #{dishFlavor}, `number` = #{number}, amount = #{amount},
WHERE id = #{id};

mapper.delete:
DELETE FROM shopping_cart

    AND id = #{id}
    AND `name` = #{name}
    AND user_id = #{userId}
    AND dish_id = #{dishId}
    AND setmeal_id = #{setmealId}

        AND 1=0

mapper.select:
SELECT * FROM shopping_cart

    AND user_id = #{userId}
    AND setmeal_id = #{setmealId}
    AND dish_id = #{dishId}
    AND dish_flavor = #{dishFlavor}
